import React, { Component } from "react";
import Title from "./Title";
import Image from "./Image";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hovered: false
    }
  }

  isTitleHovered = () => {
    this.setState({
      hovered: true
    });
  }
  render() {
    return (
      <div className="App">
        <div className="titles">
          <h1> selected works </h1>
          <Title text="branding" hovered = {this.state.hovered}/>
          <Title text="collages" />
          <Title text="posters" />
          <Title text="keynote designs" />
          <Title text="user interface designs" />
        </div>
        <div className="imageWrapper">
          if(this.state.hovered){
            <Image imgSrc="https://raw.githubusercontent.com/tirzatirza14/portfolio/master/spread.gif" />

          }
    </div>
      </div>
    );
  }
}

export default App;
