import React, { Component } from "react";
import "./App.css";

class Title extends Component {
  constructor(props) {
    super(props);
    this.state = {
      x: 0,
      y: 0
    };
  }
  _onMouseMove = e => {
    const width = this.refs.titleContainer.clientWidth;
    const height = this.refs.titleContainer.clientHeight;
    const oX = e.nativeEvent.offsetX / width * 400;
    const oY = e.nativeEvent.offsetY / height * 400;
    this.setState({
      x: oX,
      y: oY
    });
  };

  render() {
    const { text } = this.props;
    const { x, y } = this.state;
    const maskStyle = {
      "--maskX": x,
      "--maskY": y
    };
    return (
      <div
        className="titleContainer"
        onMouseMove={this._onMouseMove}
        ref="titleContainer"
        style={maskStyle}
      >
        <div className="titleWrapper">
          <h2>{text}</h2>
        </div>
        <div className="titleWrapper cloneWrapper">
          <h2>{text}</h2>
        </div>
        <div className="image">
        <img src="https://raw.githubusercontent.com/tirzatirza14/portfolio/master/spread.gif" />
      </div>
      </div>
    );
  }
}

export default Title;
