import React, { Component } from "react";
import Title from "./Title";
import Image from "./Image";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      x: 0,
      y: 0,
      hovered: false
    };
  }

  _onMouseMove = e => {
    const width = this.refs.titleContainer.clientWidth;
    const height = this.refs.titleContainer.clientHeight;
    const oX = e.nativeEvent.offsetX / width * 175;
    const oY = e.nativeEvent.offsetY / height * 100;
    this.setState({
      x: oX,
      y: oY,
      hovered: true
    });
  };

  _onMouseOut = () => {
    this.setState({
      x: 0,
      y: 0,
      hovered: false
    });
  };

  render() {
    return (
      <div className="App">
        <div className="titles">
          <h1> selected works </h1>
          <Title
            text="branding"
            _onMouseOut={this._onMouseOut}
            _onMouseMove={this._onMouseMove}
            x = {this.state.x}
            y = {this.state.y}
          />
          <Title text="collages" />
          <Title text="posters" />
          <Title text="keynote designs" />
          <Title text="user interface designs" />
        </div>
        <div className="imageWrapper">
          <Image imgSrc="https://raw.githubusercontent.com/tirzatirza14/portfolio/master/spread.gif" />
        </div>
      </div>
    );
  }
}

export default App;
